                /** @var $this \yii\web\View */
                \yii\web\JqueryAsset::register($this);

                $this->registerJs('

var skin_list = [
    "skin-blue",
    "skin-black",
    "skin-red",
    "skin-yellow",
    "skin-purple",
    "skin-green",
    "skin-blue-light",
    "skin-black-light",
    "skin-red-light",
    "skin-yellow-light",
    "skin-purple-light",
    "skin-green-light"
];

function store(name, val) {
    if (typeof (Storage) !== "undefined") {
      localStorage.setItem(name, val);
    } else {
      window.alert(\'Please use a modern browser to properly view this template!\');
    }
}

var old_skin = "";
function set_old_skin() {
    change_skin(old_skin, false);
}

function change_skin(cls, save) {
if(cls == "")return;

    save = typeof save !== \'undefined\' ? save : true;
    $.each(skin_list, function (i) {
        if(jQuery("body").hasClass(skin_list[i])){
            old_skin = skin_list[i];
            jQuery("body").removeClass(skin_list[i]);
        }
    });

    $("body").addClass(cls);
    if(save)
        store(\'skin\', cls);
    return false;
}

jQuery("[data-skin]").on(\'click\', function (e) {
  e.preventDefault();
  change_skin(jQuery(this).data(\'skin\'));
}).hover(function() {
  change_skin(jQuery(this).data(\'skin\'), false);
},function() {
  set_old_skin();
});

if (typeof (Storage) !== "undefined") {
  var skin = localStorage.getItem(\'skin\');
  if(skin !== \'null\')
    change_skin(skin);

  //console.log(skin);
}

                ');

